import React, { useState } from 'react'
import { Menu, Icon } from 'semantic-ui-react';
import {Link} from 'react-router-dom';
import '../App.css'

const MenuBar2 = (props, color, inverted) => {

    const pathname = window.location.pathname;
    let path = pathname === '/' ? 'home' : pathname.substr(1);

    const handleItemClick = (e, { name }) => setActiveItem(name);
    const [activeItem, setActiveItem] = useState(path);

    console.log(props.inverted);
    console.log(props.color);
  console.log(props.color === "white"? "is inverted": "not inverted")

    return (
     // class={props.inverted? "ui inverted menu": ""}  {...props.color === "white"? inverted: ''}color={props.inverted? "black" : ""}
    <Menu pointing secondary inverted color={props.inverted? "grey" : ""} size="massive" >
    <Menu.Menu position="left">
    <Menu.Item name = "home"
    active={activeItem === 'home'}
    onClick={handleItemClick}
    as={Link} to="/" />

    <Menu.Item name = "games"
    active={activeItem === 'games'}
    onClick={handleItemClick}
    as={Link} to="/games">   
    <Icon name='gamepad' />
        Games
    </Menu.Item>

    </Menu.Menu>
   
    <p id='navbarName'> 
    <Icon name='laptop'  color={props.color} /> 
    <Icon name='language'  color={props.color}/> 
   
    Kimberly's Portfolio 
    
    <Icon name='image outline'    color={props.color}/>
    <Icon name='gamepad'  color={props.color} />
    </p>
    <Menu.Menu position="right"/>
    <Menu.Item  name = "Github"
    active={activeItem === 'Github'}
    onClick={handleItemClick}
    href='https://github.com/KimberlyModeste'
    target='_blank'>
    <Icon name='github' size='large'/>
    </Menu.Item>
   
    <Menu.Item 
    name = "LinkedIn"
    active={activeItem === 'LinkenIn'}
    onClick={handleItemClick}
    href='https://www.linkedin.com/in/kimberly-modeste1'
    target='_blank'>
    <Icon name='linkedin' size='large'/>
    </Menu.Item>

    <Menu.Item name = "master_resume"
    active={activeItem === 'master_resume'}
    onClick={handleItemClick}
    as={Link} to="/master_resume.pdf" >
   
    <Icon name='file alternate outline' size='large'/>
    </Menu.Item>
    </Menu>
  
 );



}
export default MenuBar2;
